import React, { PureComponent, Fragment } from 'react';
import { Table, Icon } from 'antd';
import styles from './index.less';

class BlankList extends PureComponent {
  renderNoData(emptyText,visible) {
    if (emptyText == undefined || emptyText == null) {
      emptyText = '暂无数据';
    }
    return (
      <div style={{position: 'relative', width:"100%",height:"200px",display:visible?"none":""}}>
        <div style={{ fontSize: 18,position: 'absolute',top: "100px",textAlign:"center" ,width:"100%"}}>
          {/*<Icon type="inbox" style={{ fontSize: 90 ,color: '#ddd' ,opacity: '0.35 '}}/>*/}

          <img src="data:image/svg+xml;base64," alt=""/>
          {/*<img src="../../assets/public/empty.png" style={{position:'absolute',width:200,height: 300,top:30}} alt=""/>*/}
          <div>{emptyText}</div>
        </div>
      </div>
    );
  }

  render() {
    const { emptyText: emptyText,visible, ...style } = this.props;
    return this.renderNoData(emptyText,visible);
  }
}
export default BlankList;
